import logo from "./logo.svg";
import "./App.css";
import { React, useState } from "react";

import teaData from "./assets/tea-data.json";
import DisplayItem from "./components/DisplayItem";

function App() {
  const [displayList, setDisplayList] = useState([])
  const [cart, setCart] = useState([]);

  /*
    "name": "Jasmine",
    "caffeine": 25,
    "description": "Calming and sweet",
    "price": 7.99,
    "image": "images/jasmine-iroh.jpeg"
  */
  const teas = teaData.map((item, index) => makeDisplayItem(item, index));

  function makeDisplayItem(item, index) {
    return (
      <DisplayItem
        item={item}
        id={index}
        buttonClick={() => setCart(makeCartItem(item, index))}
      />
    );
  }

  function makeCartItem(item, index) {
    return (
      <CartItem
        item={item}
        id={index}
        buttonClick={setCart(makeCartItem(item, index))}
      />
    );
  }

  return (
    <div className="App">
      <header className="Morgann's Teas">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          hell
        </p>
      </header>

      <div className="Display">
        <h1>Select your Teas!</h1>
        {/* {teas} */}
      </div>

      <div className="Cart">
        <h1>Cart</h1>
        {cart}
      </div>
    </div>
  );
}

export default App;
