import logo from "./logo.svg";
import "./App.css";
import { React, useState } from "react";

import teaData from "./assets/tea-data.json";
import DisplayItem from "./components/DisplayItem";
import CartItem from "./components/CartItem";

function App() {
  // DISPLAY := list of all tea json objects. Will be filtered/sorted later
  const [displayList, setDisplayList] = useState(teaData);
  // By default, load all teas. Will be filtered/sorted later
  // const allTeas = teaData.map((item, index) => makeDisplayItem(item, index));
  // setDisplayList(allTeas);

  // CART stores all items, total cost
  // CART ITEMS: store all tea json object items in cart by default, with additional quantity field set to 0, id: index
  const [cartList, setCartList] = useState({
    cartItems: teaData.map((item, index) => {
      return { ...item, quantity: 0, id: index };
    }),
    totalCost: 0,
  });
  // ADD: functionally maps with id==index -> quantity + 1, totalCost + price
  function addToCart(id) {
    const newItems = cartList.cartItems.map((item, index) =>
      index === id ? { ...item, quantity: item.quantity + 1 } : item
    );
    setCartList({
      cartItems: newItems,
      totalCost: cartList.totalCost + teaData[id].price,
    });
  }

  function removeFromCart(id) {
    const newItems = cartList.cartItems.map((item, index) =>
      index === id ? { ...item, quantity: item.quantity - 1 } : item
    );
    setCartList({
      cartItems: newItems,
      totalCost: cartList.totalCost - teaData[id].price,
    });
  }


  function makeDisplayItem(item, id) {
    return (
      <DisplayItem
        item={item}
        id={id}
        buttonClick={() => setCartList(cartList + makeCartItem(item, id))}
      />
    );
  }

  function makeCartItem(item, id) {
    // check if cart item exists by id

    return (
      <CartItem
        item={item}
        id={id}
        // remove
        buttonClick={() => 2}
      />
    );
  }

  return (
    <div className="App">
      <header className="Morgann's Teas">
        <img src={logo} className="App-logo" alt="logo" />
        <p>hell</p>
      </header>

      <div className="Display">
        <h1>Select your Teas!</h1>
        {displayList.map((item) => <DisplayItem
        item={item}
        // id={id}
        buttonClick={() => setCartList(cartList + makeCartItem(item, id))}
      />)}
      </div>

      <div className="Cart">
        <h1>Cart</h1>
        {cartList}
      </div>
    </div>
  );
}

export default App;
