import logo from "./logo.svg";
import "./App.css";
import { React, useState } from "react";

import teaData from "./assets/tea-data.json";
import DisplayItem from "./components/DisplayItem";
import CartItem from "./components/CartItem";

function App() {
  const [displayList, setDisplayList] = useState([]);
  const [cartList, setCartList] = useState([]);
  // By default, load all teas. Will be filtered/sorted later
  const allTeas = teaData.map((item, index) => makeDisplayItem(item, index));
  setDisplayList(allTeas);

  /*
    "name": "Jasmine",
    "caffeine": 25,
    "description": "Calming and sweet",
    "price": 7.99,
    "image": "images/jasmine-iroh.jpeg"
  */

  function makeDisplayItem(item, id) {
    return (
      <DisplayItem
        item={item}
        id={id}
        buttonClick={() => setCartList(makeCartItem(item, id))}
      />
    );
  }

  function makeCartItem(item, id) {
    // check cart
    return (
      <CartItem
        item={item}
        id={index}
        buttonClick={() => setCartList(makeCartItem(item, index))}
      />
    );
  }

  return (
    <div className="App">
      <header className="Morgann's Teas">
        <img src={logo} className="App-logo" alt="logo" />
        <p>hell</p>
      </header>

      <div className="Display">
        <h1>Select your Teas!</h1>
        {displayList}
      </div>

      <div className="Cart">
        <h1>Cart</h1>
        {cartList}
      </div>
    </div>
  );
}

export default App;
